contracts/AgroExportContract.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

/**
 * @title AgroExportContract
 * @dev Smart contract para gestin de contratos de exportacin agrcola
 * @author Triboka Team
 */
contract AgroExportContract is AccessControl, ReentrancyGuard, Pausable {
    // Roles
    bytes32 public constant EXPORTER_ROLE = keccak256("EXPORTER_ROLE");
    bytes32 public constant BUYER_ROLE = keccak256("BUYER_ROLE");
    bytes32 public constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");

    // Estados del contrato
    enum ContractStatus { Active, Completed, Cancelled, Expired }

    // Estructura del contrato de exportacin
    struct ExportContract {
        bytes32 contractId;
        address buyer;              // Direccin del comprador
        address exporter;           // Direccin de la exportadora
        string contractCode;        // Cdigo nico del contrato
        string productType;         // Tipo de producto (cacao, caf, etc.)
        string productGrade;        // Grado de calidad
        uint256 totalVolumeMT;      // Volumen total en toneladas mtricas
        int256 differentialUSD;     // Diferencial vs precio spot (puede ser negativo)
        uint256 contractDate;       // Fecha de creacin del contrato
        uint256 startDate;          // Fecha de inicio
        uint256 endDate;            // Fecha de fin
        uint256 deliveryDate;       // Fecha de entrega
        uint256 fixedVolumeMT;      // Volumen ya fijado
        ContractStatus status;      // Estado del contrato
        bool exists;                // Flag de existencia
    }

    // Estructura de fijacin
    struct Fixation {
        bytes32 fixationId;
        bytes32 contractId;
        uint256 fixedQuantityMT;    // Cantidad fijada en TM
        uint256 spotPriceUSD;       // Precio spot en USD/TM (con 2 decimales)
        uint256 finalPriceUSD;      // Precio final (spot + diferencial)
        uint256 fixationDate;       // Fecha de la fijacin
        uint256[] lotIds;           // IDs de lotes asociados
        string notes;               // Observaciones
        address registeredBy;       // Quin registr la fijacin
        bool exists;                // Flag de existencia
    }

    // Mappings
    mapping(bytes32 => ExportContract) public contracts;
    mapping(bytes32 => Fixation) public fixations;
    mapping(address => bytes32[]) public contractsByExporter;
    mapping(address => bytes32[]) public contractsByBuyer;
    mapping(bytes32 => bytes32[]) public fixationsByContract;

    // Contadores
    uint256 public totalContracts;
    uint256 public totalFixations;

    // Events
    event ContractCreated(
        bytes32 indexed contractId,
        address indexed buyer,
        address indexed exporter,
        string contractCode,
        uint256 totalVolumeMT
    );

    event FixationRegistered(
        bytes32 indexed fixationId,
        bytes32 indexed contractId,
        uint256 fixedQuantityMT,
        uint256 spotPriceUSD,
        uint256 finalPriceUSD,
        uint256[] lotIds
    );

    event ContractStatusChanged(
        bytes32 indexed contractId,
        ContractStatus oldStatus,
        ContractStatus newStatus
    );

    event ContractCompleted(
        bytes32 indexed contractId,
        uint256 totalFixedVolume,
        uint256 totalFixations
    );

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(OPERATOR_ROLE, msg.sender);
    }

    /**
     * @dev Crear nuevo contrato de exportacin
     */
    function createContract(
        address _buyer,
        address _exporter,
        string memory _contractCode,
        string memory _productType,
        string memory _productGrade,
        uint256 _totalVolumeMT,
        int256 _differentialUSD,
        uint256 _startDate,
        uint256 _endDate,
        uint256 _deliveryDate
    ) external onlyRole(OPERATOR_ROLE) whenNotPaused returns (bytes32) {
        require(_buyer != address(0), "Invalid buyer address");
        require(_exporter != address(0), "Invalid exporter address");
        require(_totalVolumeMT > 0, "Volume must be greater than 0");
        require(_startDate < _endDate, "Invalid date range");
        require(_deliveryDate >= _endDate, "Delivery date must be after end date");

        bytes32 contractId = keccak256(abi.encodePacked(
            _contractCode,
            _buyer,
            _exporter,
            block.timestamp,
            totalContracts
        ));

        require(!contracts[contractId].exists, "Contract already exists");

        contracts[contractId] = ExportContract({
            contractId: contractId,
            buyer: _buyer,
            exporter: _exporter,
            contractCode: _contractCode,
            productType: _productType,
            productGrade: _productGrade,
            totalVolumeMT: _totalVolumeMT,
            differentialUSD: _differentialUSD,
            contractDate: block.timestamp,
            startDate: _startDate,
            endDate: _endDate,
            deliveryDate: _deliveryDate,
            fixedVolumeMT: 0,
            status: ContractStatus.Active,
            exists: true
        });

        // Agregar a ndices
        contractsByExporter[_exporter].push(contractId);
        contractsByBuyer[_buyer].push(contractId);
        totalContracts++;

        // Otorgar roles si no los tienen
        if (!hasRole(BUYER_ROLE, _buyer)) {
            _grantRole(BUYER_ROLE, _buyer);
        }
        if (!hasRole(EXPORTER_ROLE, _exporter)) {
            _grantRole(EXPORTER_ROLE, _exporter);
        }

        emit ContractCreated(contractId, _buyer, _exporter, _contractCode, _totalVolumeMT);
        return contractId;
    }

    /**
     * @dev Registrar nueva fijacin
     */
    function registerFixation(
        bytes32 _contractId,
        uint256 _fixedQuantityMT,
        uint256 _spotPriceUSD,
        uint256[] memory _lotIds,
        string memory _notes
    ) external whenNotPaused returns (bytes32) {
        ExportContract storage contractData = contracts[_contractId];
        require(contractData.exists, "Contract does not exist");
        require(contractData.status == ContractStatus.Active, "Contract not active");
        require(
            hasRole(EXPORTER_ROLE, msg.sender) || hasRole(OPERATOR_ROLE, msg.sender),
            "Not authorized to register fixations"
        );

        // Validar que el exportador sea correcto
        if (hasRole(EXPORTER_ROLE, msg.sender)) {
            require(contractData.exporter == msg.sender, "Not the contract exporter");
        }

        // Validaciones de negocio
        require(_fixedQuantityMT > 0, "Fixed quantity must be greater than 0");
        require(_spotPriceUSD > 0, "Spot price must be greater than 0");

        uint256 pendingVolume = contractData.totalVolumeMT - contractData.fixedVolumeMT;
        require(_fixedQuantityMT <= pendingVolume, "Fixed quantity exceeds pending volume");

        // Verificar fechas
        require(block.timestamp >= contractData.startDate, "Contract period not started");
        require(block.timestamp <= contractData.endDate, "Contract period expired");

        // Calcular precio final
        uint256 finalPriceUSD;
        if (contractData.differentialUSD >= 0) {
            finalPriceUSD = _spotPriceUSD + uint256(contractData.differentialUSD);
        } else {
            uint256 absDifferential = uint256(-contractData.differentialUSD);
            require(_spotPriceUSD >= absDifferential, "Spot price too low for differential");
            finalPriceUSD = _spotPriceUSD - absDifferential;
        }

        // Crear ID de fijacin
        bytes32 fixationId = keccak256(abi.encodePacked(
            _contractId,
            _fixedQuantityMT,
            _spotPriceUSD,
            block.timestamp,
            totalFixations
        ));

        require(!fixations[fixationId].exists, "Fixation already exists");

        // Registrar fijacin
        fixations[fixationId] = Fixation({
            fixationId: fixationId,
            contractId: _contractId,
            fixedQuantityMT: _fixedQuantityMT,
            spotPriceUSD: _spotPriceUSD,
            finalPriceUSD: finalPriceUSD,
            fixationDate: block.timestamp,
            lotIds: _lotIds,
            notes: _notes,
            registeredBy: msg.sender,
            exists: true
        });

        // Actualizar contrato
        contractData.fixedVolumeMT += _fixedQuantityMT;
        fixationsByContract[_contractId].push(fixationId);
        totalFixations++;

        // Verificar si el contrato se complet
        if (contractData.fixedVolumeMT >= contractData.totalVolumeMT) {
            _changeContractStatus(_contractId, ContractStatus.Completed);
            emit ContractCompleted(_contractId, contractData.fixedVolumeMT, fixationsByContract[_contractId].length);
        }

        emit FixationRegistered(fixationId, _contractId, _fixedQuantityMT, _spotPriceUSD, finalPriceUSD, _lotIds);
        return fixationId;
    }

    /**
     * @dev Cambiar estado del contrato
     */
    function changeContractStatus(bytes32 _contractId, ContractStatus _newStatus) 
        external onlyRole(OPERATOR_ROLE) {
        _changeContractStatus(_contractId, _newStatus);
    }

    function _changeContractStatus(bytes32 _contractId, ContractStatus _newStatus) internal {
        ExportContract storage contractData = contracts[_contractId];
        require(contractData.exists, "Contract does not exist");

        ContractStatus oldStatus = contractData.status;
        contractData.status = _newStatus;

        emit ContractStatusChanged(_contractId, oldStatus, _newStatus);
    }

    /**
     * @dev Obtener informacin del contrato
     */
    function getContract(bytes32 _contractId) external view returns (
        address buyer,
        address exporter,
        string memory contractCode,
        string memory productType,
        string memory productGrade,
        uint256 totalVolumeMT,
        int256 differentialUSD,
        uint256 contractDate,
        uint256 startDate,
        uint256 endDate,
        uint256 deliveryDate,
        uint256 fixedVolumeMT,
        ContractStatus status
    ) {
        ExportContract storage contractData = contracts[_contractId];
        require(contractData.exists, "Contract does not exist");

        return (
            contractData.buyer,
            contractData.exporter,
            contractData.contractCode,
            contractData.productType,
            contractData.productGrade,
            contractData.totalVolumeMT,
            contractData.differentialUSD,
            contractData.contractDate,
            contractData.startDate,
            contractData.endDate,
            contractData.deliveryDate,
            contractData.fixedVolumeMT,
            contractData.status
        );
    }

    /**
     * @dev Obtener informacin de fijacin
     */
    function getFixation(bytes32 _fixationId) external view returns (
        bytes32 contractId,
        uint256 fixedQuantityMT,
        uint256 spotPriceUSD,
        uint256 finalPriceUSD,
        uint256 fixationDate,
        uint256[] memory lotIds,
        string memory notes,
        address registeredBy
    ) {
        Fixation storage fixation = fixations[_fixationId];
        require(fixation.exists, "Fixation does not exist");

        return (
            fixation.contractId,
            fixation.fixedQuantityMT,
            fixation.spotPriceUSD,
            fixation.finalPriceUSD,
            fixation.fixationDate,
            fixation.lotIds,
            fixation.notes,
            fixation.registeredBy
        );
    }

    /**
     * @dev Obtener contratos por exportadora
     */
    function getContractsByExporter(address _exporter) external view returns (bytes32[] memory) {
        return contractsByExporter[_exporter];
    }

    /**
     * @dev Obtener contratos por comprador
     */
    function getContractsByBuyer(address _buyer) external view returns (bytes32[] memory) {
        return contractsByBuyer[_buyer];
    }

    /**
     * @dev Obtener fijaciones por contrato
     */
    function getFixationsByContract(bytes32 _contractId) external view returns (bytes32[] memory) {
        return fixationsByContract[_contractId];
    }

    /**
     * @dev Obtener volumen pendiente del contrato
     */
    function getPendingVolume(bytes32 _contractId) external view returns (uint256) {
        ExportContract storage contractData = contracts[_contractId];
        require(contractData.exists, "Contract does not exist");
        return contractData.totalVolumeMT - contractData.fixedVolumeMT;
    }

    /**
     * @dev Verificar si el contrato est vencido
     */
    function isContractExpired(bytes32 _contractId) external view returns (bool) {
        ExportContract storage contractData = contracts[_contractId];
        require(contractData.exists, "Contract does not exist");
        return block.timestamp > contractData.endDate && contractData.status == ContractStatus.Active;
    }

    /**
     * @dev Marcar contratos vencidos (funcin administrativa)
     */
    function markExpiredContracts(bytes32[] memory _contractIds) external onlyRole(OPERATOR_ROLE) {
        for (uint256 i = 0; i < _contractIds.length; i++) {
            if (this.isContractExpired(_contractIds[i])) {
                _changeContractStatus(_contractIds[i], ContractStatus.Expired);
            }
        }
    }

    /**
     * @dev Funciones de pausa para emergencias
     */
    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    /**
     * @dev Obtener estadsticas generales
     */
    function getStats() external view returns (
        uint256 _totalContracts,
        uint256 _totalFixations,
        uint256 activeContracts,
        uint256 completedContracts
    ) {
        // Esta funcin requerira iterar sobre todos los contratos para contar activos/completados
        // En una implementacin de produccin, se mantendran contadores separados
        return (totalContracts, totalFixations, 0, 0);
    }
}

contracts/DocumentRegistry.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

/**
 * @title DocumentRegistry
 * @dev Registro de documentos con verificacin blockchain para trazabilidad
 * @author Triboka Team
 */
contract DocumentRegistry is AccessControl, ReentrancyGuard, Pausable {
    // Roles
    bytes32 public constant ISSUER_ROLE = keccak256("ISSUER_ROLE");
    bytes32 public constant VERIFIER_ROLE = keccak256("VERIFIER_ROLE");
    bytes32 public constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");

    // Tipos de documentos
    enum DocumentType { 
        Invoice,           // Factura comercial
        Certificate,       // Certificado (fitosanitario, calidad, etc.)
        BillOfLading,      // Conocimiento de embarque
        PackingList,       // Lista de empaque
        Insurance,         // Pliza de seguro
        CustomsDeclaration,// Declaracin aduanera
        QualityReport,     // Reporte de calidad
        Contract,          // Contrato
        Other              // Otros documentos
    }

    // Estados del documento
    enum DocumentStatus { 
        Issued,      // Emitido
        Verified,    // Verificado
        Rejected,    // Rechazado
        Revoked      // Revocado
    }

    // Estructura del documento
    struct Document {
        bytes32 documentId;
        bytes32 entityId;          // ID de entidad relacionada (contrato, embarque, etc.)
        DocumentType docType;
        string documentName;       // Nombre del documento
        bytes32 documentHash;      // Hash SHA-256 del documento
        string ipfsHash;           // Hash IPFS para almacenamiento descentralizado
        address issuer;            // Quien emiti el documento
        address verifier;          // Quien verific (si aplica)
        uint256 issuedDate;        // Fecha de emisin
        uint256 verifiedDate;      // Fecha de verificacin
        uint256 expiryDate;        // Fecha de vencimiento (0 si no vence)
        DocumentStatus status;     // Estado actual
        string metadata;           // Metadatos adicionales (JSON)
        bool exists;               // Flag de existencia
    }

    // Mappings
    mapping(bytes32 => Document) public documents;
    mapping(bytes32 => bytes32[]) public documentsByEntity;  // Por contrato/embarque
    mapping(address => bytes32[]) public documentsByIssuer;
    mapping(DocumentType => bytes32[]) public documentsByType;
    mapping(bytes32 => bytes32[]) public documentsByHash;    // Para detectar duplicados

    // Contadores
    uint256 public totalDocuments;
    mapping(DocumentType => uint256) public documentCountByType;
    mapping(DocumentStatus => uint256) public documentCountByStatus;

    // Events
    event DocumentIssued(
        bytes32 indexed documentId,
        bytes32 indexed entityId,
        DocumentType indexed docType,
        address issuer,
        bytes32 documentHash
    );

    event DocumentVerified(
        bytes32 indexed documentId,
        address indexed verifier,
        uint256 verifiedDate
    );

    event DocumentStatusChanged(
        bytes32 indexed documentId,
        DocumentStatus oldStatus,
        DocumentStatus newStatus,
        address changedBy
    );

    event DocumentRevoked(
        bytes32 indexed documentId,
        address revokedBy,
        string reason
    );

    constructor() {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(OPERATOR_ROLE, msg.sender);
        _grantRole(ISSUER_ROLE, msg.sender);
        _grantRole(VERIFIER_ROLE, msg.sender);
    }

    /**
     * @dev Emitir nuevo documento
     */
    function issueDocument(
        bytes32 _entityId,
        DocumentType _docType,
        string memory _documentName,
        bytes32 _documentHash,
        string memory _ipfsHash,
        uint256 _expiryDate,
        string memory _metadata
    ) external onlyRole(ISSUER_ROLE) whenNotPaused returns (bytes32) {
        require(_entityId != bytes32(0), "Invalid entity ID");
        require(_documentHash != bytes32(0), "Invalid document hash");
        require(bytes(_documentName).length > 0, "Document name required");

        // Verificar que el hash no exista ya para la misma entidad
        bytes32[] memory existingDocs = documentsByHash[_documentHash];
        for (uint256 i = 0; i < existingDocs.length; i++) {
            Document storage existingDoc = documents[existingDocs[i]];
            require(existingDoc.entityId != _entityId || existingDoc.status == DocumentStatus.Revoked, 
                   "Document with same hash already exists for this entity");
        }

        // Generar ID nico del documento
        bytes32 documentId = keccak256(abi.encodePacked(
            _entityId,
            _documentHash,
            msg.sender,
            block.timestamp,
            totalDocuments
        ));

        require(!documents[documentId].exists, "Document ID collision");

        // Crear documento
        documents[documentId] = Document({
            documentId: documentId,
            entityId: _entityId,
            docType: _docType,
            documentName: _documentName,
            documentHash: _documentHash,
            ipfsHash: _ipfsHash,
            issuer: msg.sender,
            verifier: address(0),
            issuedDate: block.timestamp,
            verifiedDate: 0,
            expiryDate: _expiryDate,
            status: DocumentStatus.Issued,
            metadata: _metadata,
            exists: true
        });

        // Actualizar ndices
        documentsByEntity[_entityId].push(documentId);
        documentsByIssuer[msg.sender].push(documentId);
        documentsByType[_docType].push(documentId);
        documentsByHash[_documentHash].push(documentId);

        // Actualizar contadores
        totalDocuments++;
        documentCountByType[_docType]++;
        documentCountByStatus[DocumentStatus.Issued]++;

        emit DocumentIssued(documentId, _entityId, _docType, msg.sender, _documentHash);
        return documentId;
    }

    /**
     * @dev Verificar documento
     */
    function verifyDocument(bytes32 _documentId) external onlyRole(VERIFIER_ROLE) whenNotPaused {
        Document storage doc = documents[_documentId];
        require(doc.exists, "Document does not exist");
        require(doc.status == DocumentStatus.Issued, "Document not in issued status");
        require(doc.expiryDate == 0 || doc.expiryDate > block.timestamp, "Document expired");

        // Actualizar documento
        doc.verifier = msg.sender;
        doc.verifiedDate = block.timestamp;

        _changeDocumentStatus(_documentId, DocumentStatus.Verified);

        emit DocumentVerified(_documentId, msg.sender, block.timestamp);
    }

    /**
     * @dev Rechazar documento
     */
    function rejectDocument(bytes32 _documentId, string memory _reason) 
        external onlyRole(VERIFIER_ROLE) whenNotPaused {
        Document storage doc = documents[_documentId];
        require(doc.exists, "Document does not exist");
        require(doc.status == DocumentStatus.Issued, "Document not in issued status");

        _changeDocumentStatus(_documentId, DocumentStatus.Rejected);
    }

    /**
     * @dev Revocar documento
     */
    function revokeDocument(bytes32 _documentId, string memory _reason) 
        external whenNotPaused {
        Document storage doc = documents[_documentId];
        require(doc.exists, "Document does not exist");
        require(
            msg.sender == doc.issuer || 
            hasRole(OPERATOR_ROLE, msg.sender) || 
            hasRole(DEFAULT_ADMIN_ROLE, msg.sender),
            "Not authorized to revoke"
        );
        require(doc.status != DocumentStatus.Revoked, "Document already revoked");

        _changeDocumentStatus(_documentId, DocumentStatus.Revoked);

        emit DocumentRevoked(_documentId, msg.sender, _reason);
    }

    /**
     * @dev Cambiar estado del documento
     */
    function _changeDocumentStatus(bytes32 _documentId, DocumentStatus _newStatus) internal {
        Document storage doc = documents[_documentId];
        DocumentStatus oldStatus = doc.status;

        // Actualizar contadores
        documentCountByStatus[oldStatus]--;
        documentCountByStatus[_newStatus]++;

        // Cambiar estado
        doc.status = _newStatus;

        emit DocumentStatusChanged(_documentId, oldStatus, _newStatus, msg.sender);
    }

    /**
     * @dev Verificar integridad del documento comparando hashes
     */
    function verifyDocumentIntegrity(bytes32 _documentId, bytes32 _providedHash) 
        external view returns (bool) {
        Document storage doc = documents[_documentId];
        require(doc.exists, "Document does not exist");
        return doc.documentHash == _providedHash;
    }

    /**
     * @dev Verificar si el documento est vigente
     */
    function isDocumentValid(bytes32 _documentId) external view returns (bool) {
        Document storage doc = documents[_documentId];
        if (!doc.exists) return false;
        if (doc.status == DocumentStatus.Revoked || doc.status == DocumentStatus.Rejected) return false;
        if (doc.expiryDate != 0 && doc.expiryDate <= block.timestamp) return false;
        return true;
    }

    /**
     * @dev Obtener informacin del documento
     */
    function getDocument(bytes32 _documentId) external view returns (
        bytes32 entityId,
        DocumentType docType,
        string memory documentName,
        bytes32 documentHash,
        string memory ipfsHash,
        address issuer,
        address verifier,
        uint256 issuedDate,
        uint256 verifiedDate,
        uint256 expiryDate,
        DocumentStatus status,
        string memory metadata
    ) {
        Document storage doc = documents[_documentId];
        require(doc.exists, "Document does not exist");

        return (
            doc.entityId,
            doc.docType,
            doc.documentName,
            doc.documentHash,
            doc.ipfsHash,
            doc.issuer,
            doc.verifier,
            doc.issuedDate,
            doc.verifiedDate,
            doc.expiryDate,
            doc.status,
            doc.metadata
        );
    }

    /**
     * @dev Obtener documentos por entidad (contrato, embarque, etc.)
     */
    function getDocumentsByEntity(bytes32 _entityId) external view returns (bytes32[] memory) {
        return documentsByEntity[_entityId];
    }

    /**
     * @dev Obtener documentos por emisor
     */
    function getDocumentsByIssuer(address _issuer) external view returns (bytes32[] memory) {
        return documentsByIssuer[_issuer];
    }

    /**
     * @dev Obtener documentos por tipo
     */
    function getDocumentsByType(DocumentType _docType) external view returns (bytes32[] memory) {
        return documentsByType[_docType];
    }

    /**
     * @dev Verificar si una entidad tiene todos los documentos requeridos
     */
    function hasRequiredDocuments(
        bytes32 _entityId, 
        DocumentType[] memory _requiredTypes
    ) external view returns (bool) {
        bytes32[] memory entityDocs = documentsByEntity[_entityId];

        for (uint256 i = 0; i < _requiredTypes.length; i++) {
            bool hasType = false;
            for (uint256 j = 0; j < entityDocs.length; j++) {
                Document storage doc = documents[entityDocs[j]];
                if (doc.docType == _requiredTypes[i] && 
                    doc.status == DocumentStatus.Verified &&
                    (doc.expiryDate == 0 || doc.expiryDate > block.timestamp)) {
                    hasType = true;
                    break;
                }
            }
            if (!hasType) return false;
        }
        return true;
    }

    /**
     * @dev Obtener documentos prximos a vencer
     */
    function getExpiringDocuments(uint256 _daysBeforeExpiry) 
        external view returns (bytes32[] memory) {
        // Esta funcin requerira iterar sobre todos los documentos
        // En produccin se implementara con eventos o ndices adicionales
        bytes32[] memory expiring = new bytes32[](0);
        return expiring;
    }

    /**
     * @dev Obtener estadsticas de documentos
     */
    function getDocumentStats() external view returns (
        uint256 _totalDocuments,
        uint256 issuedCount,
        uint256 verifiedCount,
        uint256 rejectedCount,
        uint256 revokedCount
    ) {
        return (
            totalDocuments,
            documentCountByStatus[DocumentStatus.Issued],
            documentCountByStatus[DocumentStatus.Verified],
            documentCountByStatus[DocumentStatus.Rejected],
            documentCountByStatus[DocumentStatus.Revoked]
        );
    }

    /**
     * @dev Funciones de pausa para emergencias
     */
    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    /**
     * @dev Batch operations para eficiencia
     */
    function batchIssueDocuments(
        bytes32[] memory _entityIds,
        DocumentType[] memory _docTypes,
        string[] memory _documentNames,
        bytes32[] memory _documentHashes,
        string[] memory _ipfsHashes,
        uint256[] memory _expiryDates,
        string[] memory _metadatas
    ) external onlyRole(ISSUER_ROLE) whenNotPaused returns (bytes32[] memory) {
        require(_entityIds.length == _docTypes.length, "Array length mismatch");
        require(_entityIds.length == _documentNames.length, "Array length mismatch");
        require(_entityIds.length == _documentHashes.length, "Array length mismatch");
        require(_entityIds.length == _ipfsHashes.length, "Array length mismatch");
        require(_entityIds.length == _expiryDates.length, "Array length mismatch");
        require(_entityIds.length == _metadatas.length, "Array length mismatch");

        bytes32[] memory documentIds = new bytes32[](_entityIds.length);

        for (uint256 i = 0; i < _entityIds.length; i++) {
            documentIds[i] = this.issueDocument(
                _entityIds[i],
                _docTypes[i],
                _documentNames[i],
                _documentHashes[i],
                _ipfsHashes[i],
                _expiryDates[i],
                _metadatas[i]
            );
        }

        return documentIds;
    }
}

contracts/ProducerLotNFT.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

/**
 * @title ProducerLotNFT
 * @dev NFT contract para lotes de productores agrcolas con trazabilidad completa
 * @author Triboka Team
 */
contract ProducerLotNFT is ERC721, ERC721URIStorage, ERC721Enumerable, AccessControl, Pausable {
    using Counters for Counters.Counter;

    // Roles
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    bytes32 public constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");
    bytes32 public constant PRODUCER_ROLE = keccak256("PRODUCER_ROLE");

    // Contador de tokens
    Counters.Counter private _tokenIdCounter;

    // Estados del lote
    enum LotStatus { Created, Purchased, Fixed, Shipped, Delivered }

    // Estructura del lote de productor
    struct ProducerLot {
        uint256 lotId;
        address producer;           // Direccin del productor
        string producerName;        // Nombre del productor
        string farmName;            // Nombre de la finca
        string location;            // Ubicacin GPS (lat,lng)
        string productType;         // Tipo de producto (cacao, caf, etc.)
        uint256 weightKg;           // Peso neto en kilogramos
        string qualityGrade;        // Grado de calidad
        uint256 harvestDate;        // Fecha de cosecha (timestamp)
        uint256 purchaseDate;       // Fecha de compra (timestamp)
        uint256 purchasePriceUSD;   // Precio de compra en USD (con 2 decimales)
        string[] certifications;   // Certificaciones (orgnico, fair trade, etc.)
        LotStatus status;           // Estado actual del lote
        bytes32 contractId;         // ID del contrato al que fue asignado (si aplica)
        bytes32 shipmentId;         // ID del embarque (si aplica)
        string metadataURI;         // URI de metadatos IPFS
        bool exists;                // Flag de existencia
    }

    // Mappings
    mapping(uint256 => ProducerLot) public lots;
    mapping(address => uint256[]) public lotsByProducer;
    mapping(bytes32 => uint256[]) public lotsByContract;
    mapping(bytes32 => uint256[]) public lotsByShipment;
    mapping(string => uint256) public lotsByCode;

    // Contadores por estado
    mapping(LotStatus => uint256) public lotsByStatus;

    // Events
    event LotCreated(
        uint256 indexed lotId,
        address indexed producer,
        string lotCode,
        uint256 weightKg,
        string productType
    );

    event LotPurchased(
        uint256 indexed lotId,
        address indexed buyer,
        uint256 purchasePriceUSD,
        uint256 purchaseDate
    );

    event LotFixed(
        uint256 indexed lotId,
        bytes32 indexed contractId,
        string contractCode
    );

    event LotShipped(
        uint256 indexed lotId,
        bytes32 indexed shipmentId,
        string containerNumber
    );

    event LotStatusChanged(
        uint256 indexed lotId,
        LotStatus oldStatus,
        LotStatus newStatus
    );

    event CertificationAdded(
        uint256 indexed lotId,
        string certification,
        address addedBy
    );

    constructor() ERC721("ProducerLotNFT", "PLNFT") {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);
        _grantRole(OPERATOR_ROLE, msg.sender);
    }

    /**
     * @dev Crear nuevo lote de productor y mint NFT
     */
    function createLot(
        address _producer,
        string memory _producerName,
        string memory _farmName,
        string memory _location,
        string memory _productType,
        uint256 _weightKg,
        string memory _qualityGrade,
        uint256 _harvestDate,
        string[] memory _certifications,
        string memory _metadataURI
    ) external onlyRole(MINTER_ROLE) whenNotPaused returns (uint256) {
        require(_producer != address(0), "Invalid producer address");
        require(_weightKg > 0, "Weight must be greater than 0");
        require(_harvestDate <= block.timestamp, "Harvest date cannot be in the future");

        uint256 tokenId = _tokenIdCounter.current();
        _tokenIdCounter.increment();

        // Mint NFT al productor
        _safeMint(_producer, tokenId);

        // Generar cdigo nico del lote
        string memory lotCode = string(abi.encodePacked(
            "LOT-",
            _productType,
            "-",
            _toString(block.timestamp),
            "-",
            _toString(tokenId)
        ));

        // Crear estructura del lote
        lots[tokenId] = ProducerLot({
            lotId: tokenId,
            producer: _producer,
            producerName: _producerName,
            farmName: _farmName,
            location: _location,
            productType: _productType,
            weightKg: _weightKg,
            qualityGrade: _qualityGrade,
            harvestDate: _harvestDate,
            purchaseDate: 0,
            purchasePriceUSD: 0,
            certifications: _certifications,
            status: LotStatus.Created,
            contractId: bytes32(0),
            shipmentId: bytes32(0),
            metadataURI: _metadataURI,
            exists: true
        });

        // Actualizar ndices
        lotsByProducer[_producer].push(tokenId);
        lotsByCode[lotCode] = tokenId;
        lotsByStatus[LotStatus.Created]++;

        // Otorgar role de productor si no lo tiene
        if (!hasRole(PRODUCER_ROLE, _producer)) {
            _grantRole(PRODUCER_ROLE, _producer);
        }

        // Establecer URI de metadatos
        if (bytes(_metadataURI).length > 0) {
            _setTokenURI(tokenId, _metadataURI);
        }

        emit LotCreated(tokenId, _producer, lotCode, _weightKg, _productType);
        return tokenId;
    }

    /**
     * @dev Registrar compra del lote
     */
    function purchaseLot(
        uint256 _lotId,
        address _buyer,
        uint256 _purchasePriceUSD
    ) external onlyRole(OPERATOR_ROLE) whenNotPaused {
        ProducerLot storage lot = lots[_lotId];
        require(lot.exists, "Lot does not exist");
        require(lot.status == LotStatus.Created, "Lot already purchased");
        require(_buyer != address(0), "Invalid buyer address");
        require(_purchasePriceUSD > 0, "Purchase price must be greater than 0");

        // Actualizar informacin de compra
        lot.purchaseDate = block.timestamp;
        lot.purchasePriceUSD = _purchasePriceUSD;

        // Cambiar estado
        _changeLotStatus(_lotId, LotStatus.Purchased);

        // Transferir NFT al comprador (exportadora)
        address currentOwner = ownerOf(_lotId);
        _transfer(currentOwner, _buyer, _lotId);

        emit LotPurchased(_lotId, _buyer, _purchasePriceUSD, block.timestamp);
    }

    /**
     * @dev Asignar lote a un contrato (fijacin)
     */
    function fixLotToContract(
        uint256 _lotId,
        bytes32 _contractId,
        string memory _contractCode
    ) external onlyRole(OPERATOR_ROLE) whenNotPaused {
        ProducerLot storage lot = lots[_lotId];
        require(lot.exists, "Lot does not exist");
        require(lot.status == LotStatus.Purchased, "Lot must be purchased first");
        require(_contractId != bytes32(0), "Invalid contract ID");

        // Asignar al contrato
        lot.contractId = _contractId;
        lotsByContract[_contractId].push(_lotId);

        // Cambiar estado
        _changeLotStatus(_lotId, LotStatus.Fixed);

        emit LotFixed(_lotId, _contractId, _contractCode);
    }

    /**
     * @dev Asignar lote a un embarque
     */
    function shipLot(
        uint256 _lotId,
        bytes32 _shipmentId,
        string memory _containerNumber
    ) external onlyRole(OPERATOR_ROLE) whenNotPaused {
        ProducerLot storage lot = lots[_lotId];
        require(lot.exists, "Lot does not exist");
        require(lot.status == LotStatus.Fixed, "Lot must be fixed to contract first");
        require(_shipmentId != bytes32(0), "Invalid shipment ID");

        // Asignar al embarque
        lot.shipmentId = _shipmentId;
        lotsByShipment[_shipmentId].push(_lotId);

        // Cambiar estado
        _changeLotStatus(_lotId, LotStatus.Shipped);

        emit LotShipped(_lotId, _shipmentId, _containerNumber);
    }

    /**
     * @dev Marcar lote como entregado
     */
    function deliverLot(uint256 _lotId) external onlyRole(OPERATOR_ROLE) whenNotPaused {
        ProducerLot storage lot = lots[_lotId];
        require(lot.exists, "Lot does not exist");
        require(lot.status == LotStatus.Shipped, "Lot must be shipped first");

        // Cambiar estado
        _changeLotStatus(_lotId, LotStatus.Delivered);
    }

    /**
     * @dev Cambiar estado del lote
     */
    function _changeLotStatus(uint256 _lotId, LotStatus _newStatus) internal {
        ProducerLot storage lot = lots[_lotId];
        LotStatus oldStatus = lot.status;

        // Actualizar contadores
        lotsByStatus[oldStatus]--;
        lotsByStatus[_newStatus]++;

        // Cambiar estado
        lot.status = _newStatus;

        emit LotStatusChanged(_lotId, oldStatus, _newStatus);
    }

    /**
     * @dev Agregar certificacin a un lote
     */
    function addCertification(
        uint256 _lotId,
        string memory _certification
    ) external onlyRole(OPERATOR_ROLE) whenNotPaused {
        ProducerLot storage lot = lots[_lotId];
        require(lot.exists, "Lot does not exist");
        require(bytes(_certification).length > 0, "Certification cannot be empty");

        // Verificar que no exista ya la certificacin
        for (uint256 i = 0; i < lot.certifications.length; i++) {
            require(
                keccak256(abi.encodePacked(lot.certifications[i])) != 
                keccak256(abi.encodePacked(_certification)),
                "Certification already exists"
            );
        }

        lot.certifications.push(_certification);
        emit CertificationAdded(_lotId, _certification, msg.sender);
    }

    /**
     * @dev Actualizar URI de metadatos
     */
    function updateMetadataURI(
        uint256 _lotId,
        string memory _newURI
    ) external onlyRole(OPERATOR_ROLE) whenNotPaused {
        require(_exists(_lotId), "Lot does not exist");
        lots[_lotId].metadataURI = _newURI;
        _setTokenURI(_lotId, _newURI);
    }

    /**
     * @dev Obtener informacin completa del lote
     */
    function getLot(uint256 _lotId) external view returns (
        address producer,
        string memory producerName,
        string memory farmName,
        string memory location,
        string memory productType,
        uint256 weightKg,
        string memory qualityGrade,
        uint256 harvestDate,
        uint256 purchaseDate,
        uint256 purchasePriceUSD,
        string[] memory certifications,
        LotStatus status,
        bytes32 contractId,
        bytes32 shipmentId
    ) {
        ProducerLot storage lot = lots[_lotId];
        require(lot.exists, "Lot does not exist");

        return (
            lot.producer,
            lot.producerName,
            lot.farmName,
            lot.location,
            lot.productType,
            lot.weightKg,
            lot.qualityGrade,
            lot.harvestDate,
            lot.purchaseDate,
            lot.purchasePriceUSD,
            lot.certifications,
            lot.status,
            lot.contractId,
            lot.shipmentId
        );
    }

    /**
     * @dev Obtener lotes por productor
     */
    function getLotsByProducer(address _producer) external view returns (uint256[] memory) {
        return lotsByProducer[_producer];
    }

    /**
     * @dev Obtener lotes por contrato
     */
    function getLotsByContract(bytes32 _contractId) external view returns (uint256[] memory) {
        return lotsByContract[_contractId];
    }

    /**
     * @dev Obtener lotes por embarque
     */
    function getLotsByShipment(bytes32 _shipmentId) external view returns (uint256[] memory) {
        return lotsByShipment[_shipmentId];
    }

    /**
     * @dev Obtener estadsticas de lotes
     */
    function getLotStats() external view returns (
        uint256 totalLots,
        uint256 createdLots,
        uint256 purchasedLots,
        uint256 fixedLots,
        uint256 shippedLots,
        uint256 deliveredLots
    ) {
        return (
            totalSupply(),
            lotsByStatus[LotStatus.Created],
            lotsByStatus[LotStatus.Purchased],
            lotsByStatus[LotStatus.Fixed],
            lotsByStatus[LotStatus.Shipped],
            lotsByStatus[LotStatus.Delivered]
        );
    }

    /**
     * @dev Verificar si un lote est disponible para fijacin
     */
    function isLotAvailableForFixation(uint256 _lotId) external view returns (bool) {
        ProducerLot storage lot = lots[_lotId];
        return lot.exists && lot.status == LotStatus.Purchased;
    }

    /**
     * @dev Obtener peso total de lotes por contrato
     */
    function getTotalWeightByContract(bytes32 _contractId) external view returns (uint256) {
        uint256[] memory contractLots = lotsByContract[_contractId];
        uint256 totalWeight = 0;

        for (uint256 i = 0; i < contractLots.length; i++) {
            totalWeight += lots[contractLots[i]].weightKg;
        }

        return totalWeight;
    }

    /**
     * @dev Funciones de pausa para emergencias
     */
    function pause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }

    /**
     * @dev Funciones override requeridas por herencia mltiple
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId,
        uint256 batchSize
    ) internal override(ERC721, ERC721Enumerable) whenNotPaused {
        super._beforeTokenTransfer(from, to, tokenId, batchSize);
    }

    function _burn(uint256 tokenId) internal override(ERC721, ERC721URIStorage) {
        super._burn(tokenId);
    }

    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {
        return super.tokenURI(tokenId);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721Enumerable, ERC721URIStorage, AccessControl)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

    /**
     * @dev Convertir uint256 a string
     */
    function _toString(uint256 value) internal pure returns (string memory) {
        if (value == 0) {
            return "0";
        }
        uint256 temp = value;
        uint256 digits;
        while (temp != 0) {
            digits++;
            temp /= 10;
        }
        bytes memory buffer = new bytes(digits);
        while (value != 0) {
            digits -= 1;
            buffer[digits] = bytes1(uint8(48 + uint256(value % 10)));
            value /= 10;
        }
        return string(buffer);
    }
}

